import UIKit

class ProfilePresenter: ProfilePresenterProtocol {

    weak private var view: ProfileViewProtocol?
    var interactor: ProfileInteractorProtocol?
    private let router: ProfileWireframeProtocol

    init(interface: ProfileViewProtocol, interactor: ProfileInteractorProtocol?, router: ProfileWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }
    
    func viewDidLoad() {
        view?.showLoading()
        interactor?.retrieveProfile()
    }

}

extension ProfilePresenter: ProfileInteractorOutputProtocol {
    func didRetrieveProfile(_ profile: ProfileRequest) {
        view?.hideLoading()
        view?.showProfile(with: profile)
    }
    
    func onError() {
        view?.hideLoading()
        view?.showError()
    }
}
